import React,{useState} from 'react';
import './Form.css';
import useForm from './useForm';
import validation from './validation'

const FormSignUp = () => {
    const submit=()=>{ 
        console.log('success!');
    }
    const {handleChange,handleSubmit,values,error}=useForm(submit,validation);

    return (
        <div>
            <h3>CREATE YOUR ACCOUNT</h3>
            <form onSubmit={handleSubmit}>
                <div className="form-input">
                    <div>
                        USERNAME:<input className="reg username" type="text" name="username" value={values.username} onChange={handleChange}/>
                        {error.username? <p>{error.username}</p>:''}
                    </div>
                    <div>
                        EAMIL:<input className="reg email" type="text" name="email" value={values.email} onChange={handleChange}/>
                        {error.email? <p>{error.email}</p>:''}
                    </div>
                    <div>
                        PASSWORD:<input className="reg password"type="text" name="password" value={values.password} onChange={handleChange}/> 
                        {error.password? <p>{error.password}</p>:''}
                    </div>
                </div>
                <button className="submit-register">SUBMIT</button>
                <span>Already have an account? login
                    <a href="#">here</a>
                </span>
            </form>
        </div>
    )
}

export default FormSignUp
